definition.script:
pipeline {

  // agent defines where the pipeline will run.
  agent any
  environment {
    SECURE = "TRUE"
  }
  stages {

    stage('install stage 1 - pull & configure install scripts') {
      steps {
          build 'setup-install-scripts'
      }
    }

    stage('install stage 2 - clusters un-install') {
      steps {
          build 'uninstall-clusters'
      }
    }

    stage('install stage 3 - clusters install') {
      steps {
          build 'install-clusters'
      }
    }

    stage('install stage 4 - cross-cluster tickets') {
      steps {
          build 'setup-cross-cluster-tickets'
      }
    }

    stage('install stage 5 - password-less ssh') {
      steps {
          build 'password-less-ssh'
      }
    }
  }
}
