definition.script:
pipeline {

  // agent defines where the pipeline will run.
  agent any
  environment {
    SECURE = "TRUE"
  }
  stages {
    //TODO parameterize cluster name 
    stage('install-clusters stage 1 - installing src cluster') {
      steps {
          ws('/var/jenkins_home/stress-pipeline/cluster-1') {
            echo "installing cluster 1"
            sh 'echo y | sudo ./mapr_setup.sh -c=cluster-1-roles -i -qs -f -s -m=2 -sp=2 -n=STRESS' 
          }
      }
    }
    stage('install-clusters stage 2 - installing replica cluster') {
      steps {
          ws('/var/jenkins_home/stress-pipeline/cluster-2') {
            echo "installing cluster 2"
            sh 'echo y | sudo ./mapr_setup.sh -c=cluster-2-roles -i -qs -f -s -m=2 -sp=2 -n=STRESS-REPLICA' 
          }
      }
    }
  }
}
