definition.script:
pipeline {

  // agent defines where the pipeline will run.
  agent any
  environment {
    SECURE = "TRUE"
  }
  stages {

    stage('install stage 1 - prereq for stress-pipeline scripts') {
      steps {
        echo "clean up old install if any"
        sh 'rm -rf /var/jenkins_home/stress-pipeline'
      }
    }

    stage('install stage 2 - scripts setup stage') {
      steps {
        echo "stage 2 steps"
        dir ('/var/jenkins_home/stress-pipeline/cluster-1') {
            git 'https://github.com/rohanjayaraj/mapr-cluster-scripts'
            configFileProvider([configFile(fileId: 'cluster-1-roles', 
                    targetLocation: '/var/jenkins_home/stress-pipeline/cluster-1/roles/cluster-1-roles')]) {}
            configFileProvider([configFile(fileId: 'mapr-repo', 
                    targetLocation: '/var/jenkins_home/stress-pipeline/cluster-1/repo/mapr.repo')]) {}        
        }
        dir ('/var/jenkins_home/stress-pipeline/cluster-2') {
            git 'https://github.com/rohanjayaraj/mapr-cluster-scripts'
            configFileProvider([configFile(fileId: 'cluster-2-roles', 
                    targetLocation: '/var/jenkins_home/stress-pipeline/cluster-2/roles/cluster-2-roles')]) {}
            configFileProvider([configFile(fileId: 'mapr-repo', 
                    targetLocation: '/var/jenkins_home/stress-pipeline/cluster-2/repo/mapr.repo')]) {}                
        }
        dir('/var/jenkins_home/stress-pipeline/config-scripts') {
            git branch: 'cross-cluster', url: 'git@github.com:ssinha3/private-devops-automation-framework.git'
            configFileProvider([configFile(fileId: 'cldb-list', 
                    targetLocation: '/var/jenkins_home/stress-pipeline/config-scripts/cldb-list')]) {}
        }
      }
    }

    // stage('install stage 3 - stress-pipeline un-installing src and replica') {
    //   steps {
    //       ws('/var/jenkins_home/stress-pipeline/cluster-1') {
    //         echo "un-installing cluster 1"
    //         sh 'echo y | sudo ./mapr_setup.sh -c=cluster-1-roles -u -qs -f -s -m=2 -sp=2' 
    //       }
    //       ws('/var/jenkins_home/stress-pipeline/cluster-2') {
    //         echo "un-installing cluster 2"
    //         sh 'echo y | sudo ./mapr_setup.sh -c=cluster-2-roles -u -qs -f -s -m=2 -sp=2' 
    //       }
    //   }
    // }

    // stage('install stage 4 - installing src and replica') {
    //   steps {
    //       ws('/var/jenkins_home/stress-pipeline/cluster-1') {
    //         echo "installing cluster 1"
    //         sh 'echo y | sudo ./mapr_setup.sh -c=cluster-1-roles -i -qs -f -s -m=2 -sp=2' 
    //       }
    //       ws('/var/jenkins_home/stress-pipeline/cluster-2') {
    //         echo "installing cluster 2"
    //         sh 'echo y | sudo ./mapr_setup.sh -c=cluster-2-roles -i -qs -f -s -m=2 -sp=2' 
    //       }
    //   }
    // }

    stage('install stage 5 - setup cross-cluster tickets') {
        steps {
         ws('/var/jenkins_home/stress-pipeline/config-scripts') {
            echo "setup cross cluster tickets"
            configFileProvider([configFile(fileId: 'cldb-list', 
                    variable: 'cldbs')]) {
                sh 'cat $cldbs'
                sh './bin/cross-cluster-ticket-setup  --clustMasterCLDB $cldbs'             
            }
          }   
        }
    }
  }
}
