definition.script:
pipeline {

  // agent defines where the pipeline will run.
  agent any
  environment {
    SECURE = "TRUE"
  }
  stages {

    stage('install stage 1 - prereq for stress-pipeline scripts') {
      steps {
        echo "clean up old install if any"
        sh 'rm -rf /var/jenkins_home/stress-pipeline'
      }
    }

    stage('install stage 2 - scripts setup stage') {
      steps {
        echo "stage 2 steps"
        dir ('/var/jenkins_home/stress-pipeline/cluster-1') {
            git 'https://github.com/rohanjayaraj/mapr-cluster-scripts'
            configFileProvider([configFile(fileId: 'cluster-1-roles', 
                    targetLocation: '/var/jenkins_home/stress-pipeline/cluster-1/roles/cluster-1-roles')]) {}
        }
        dir ('/var/jenkins_home/stress-pipeline/cluster-2') {
            git 'https://github.com/rohanjayaraj/mapr-cluster-scripts'
            configFileProvider([configFile(fileId: 'cluster-2-roles', 
                    targetLocation: '/var/jenkins_home/stress-pipeline/cluster-2/roles/cluster-2-roles')]) {}
        }
        dir('config-scripts') {
            //TODO: Devops framework    
            git 'git@github.com:ssinha3/private-devops-automation-framework.git'
        }
      }
    }

    stage('install stage 3 - stress-pipeline un-installing src and replica') {
      steps {
          ws('/var/jenkins_home/stress-pipeline/cluster-1') {
            echo "un-installing cluster 1"
            sh './mapr_setup.sh -c=cluster-1-roles -u -qs -f -s -m=2 -sp=2' 
          }
          ws('/var/jenkins_home/stress-pipeline/cluster-2') {
            echo "un-installing cluster 2"
            sh './mapr_setup.sh -c=cluster-2-roles -u -qs -f -s -m=2 -sp=2' 
          }
      }
    }

    stage('install stage 4 - installing src and replica') {
      steps {
          ws('/root/stress-pipeline/cluster-scripts/cluster-1') {
            echo "installing cluster 1"
            sh './mapr_setup.sh -c=cluster-1-roles -i -qs -f -s -m=2 -sp=2' 
          }
          ws('/root/stress-pipeline/cluster-scripts/cluster-2') {
            echo "installing cluster 2"
            sh './mapr_setup.sh -c=cluster-2-roles -i -qs -f -s -m=2 -sp=2' 
          }
      }
    }
  }
}
